// Inside DriverLanding.jsx
import React, { useEffect, useRef, useState } from "react";

const DrowsinessStatus = () => {
  const [ear, setEAR] = useState(0);
  const [mor, setMOR] = useState(0);
  const [status, setStatus] = useState("");
  const socketRef = useRef(null);

  useEffect(() => {
    const socket = new WebSocket("ws://localhost:8000/ws/detect");
    socketRef.current = socket;

    socket.onmessage = (event) => {
      const data = JSON.parse(event.data);
      setEAR(data.ear);
      setMOR(data.mor);
      setStatus(data.status);
    };

    return () => {
      socket.close();
    };
  }, []);

  return (
    <div style={{ background: "#111", padding: "1rem", borderRadius: "8px", color: "#fff" }}>
      <h3>Drowsiness Detection Status</h3>
      <p>EAR: {ear}</p>
      <p>MOR: {mor}</p>
      <p>Status: <strong style={{
        color: status === "SLEEPING" ? "red" :
               status === "DROWSY" ? "orange" :
               status === "ACTIVE" ? "green" : "white"
      }}>{status}</strong></p>
    </div>
  );
};

export default DrowsinessStatus;
