import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { PieChart, Pie, Cell, Tooltip, ResponsiveContainer, Sector } from "recharts";
import "../App.css";

const DriverProfile = () => {
  const [driver, setDriver] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [formData, setFormData] = useState({});
  const [timeline, setTimeline] = useState([]);
  const [showChart, setShowChart] = useState(false);
  const [activeIndex, setActiveIndex] = useState(null);

  const COLORS = ["#e74c3c", "#3498db", "#f1c40f", "#2ecc71"];

  const renderActiveShape = (props) => {
    const RADIAN = Math.PI / 180;
    const {
      cx, cy, midAngle, innerRadius, outerRadius, startAngle, endAngle,
      fill, payload, percent, value,
    } = props;
    const sin = Math.sin(-RADIAN * midAngle);
    const cos = Math.cos(-RADIAN * midAngle);
    const sx = cx + (outerRadius + 10) * cos;
    const sy = cy + (outerRadius + 10) * sin;
    const mx = cx + (outerRadius + 30) * cos;
    const my = cy + (outerRadius + 30) * sin;

    return (
      <g>
        <Sector
          cx={cx}
          cy={cy}
          innerRadius={innerRadius}
          outerRadius={outerRadius + 5}
          startAngle={startAngle}
          endAngle={endAngle}
          fill={fill}
        />
        <Sector
          cx={cx}
          cy={cy}
          startAngle={startAngle}
          endAngle={endAngle}
          innerRadius={outerRadius + 10}
          outerRadius={outerRadius + 15}
          fill={fill}
        />
        <path d={`M${sx},${sy}L${mx},${my}`} stroke={fill} fill="none" />
        <circle cx={mx} cy={my} r={2} fill={fill} stroke="none" />
        <text x={mx + (cos >= 0 ? 1 : -1) * 12} y={my} textAnchor={cos >= 0 ? "start" : "end"} fill="#999">
          {`Count: ${value}`}
        </text>
        <text x={mx + (cos >= 0 ? 1 : -1) * 12} y={my} dy={18} textAnchor={cos >= 0 ? "start" : "end"} fill="#999">
          {`(${(percent * 100).toFixed(2)}%)`}
        </text>
      </g>
    );
  };

  const navigate = useNavigate();

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("user"));
    if (user) {
      setDriver(user);
      setFormData(user);

      fetch(`http://localhost:5000/api/detections/${user.id}`)
        .then((res) => res.json())
        .then((stats) => {
          setDriver((prev) => ({ ...prev, ...stats }));
        })
        .catch((err) => console.error("Failed to fetch stats", err));

      fetch(`http://localhost:5000/api/sessions/tripCount/${user.id}`)
        .then((res) => res.json())
        .then((data) => {
          setDriver((prev) => ({ ...prev, tripCount: data.tripCount }));
        })
        .catch((err) => console.error("Failed to fetch trip count", err));

      fetch(`http://localhost:5000/api/sessions/timelines/${user.id}`)
        .then((res) => res.json())
        .then((data) => {
          if (data.timelines && Array.isArray(data.timelines)) {
            const formatted = data.timelines.map((t) =>
              new Date(t).toLocaleString("en-US", {
                dateStyle: "medium",
                timeStyle: "short",
              })
            );
            setTimeline(formatted);
          } else {
            console.warn("No timelines found in response", data);
            setTimeline([]);
          }
        })
        .catch((err) => console.error("Failed to fetch timeline", err));
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("user");
    navigate("/");
  };

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: files ? files[0] : value,
    }));
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    const data = new FormData();
    Object.keys(formData).forEach((key) => {
      data.append(key, formData[key]);
    });

    try {
      const res = await fetch(`http://localhost:5000/api/driver/update/${driver.id}`, {
        method: "PUT",
        body: data,
      });
      const updatedRes = await res.json();
      const updated = {
        ...updatedRes.driver,
        id: updatedRes.driver._id,
      };

      localStorage.setItem("user", JSON.stringify(updated));
      setDriver(updated);
      setEditMode(false);
    } catch (err) {
      alert("Update failed");
    }
  };

  if (!driver) return <p className="text-center mt-10">Loading profile...</p>;

  const data = [
    { name: "Sleepy", value: driver.sleepy ?? 0 },
    { name: "Yawn", value: driver.yawn ?? 0 },
    { name: "Drowsy", value: driver.drowsy ?? 0 },
    { name: "Active", value: driver.active ?? 0 },
  ];

  const total = data.reduce((sum, item) => sum + item.value, 0);
  const accuracy = total > 0 ? ((driver.active / total) * 100).toFixed(2) : "0.00";

  return (
    <div className="container mx-auto p-6">
      <div className="flex justify-between items-center">
        <h1 className="text-3xl font-bold mb-4">Driver Profile</h1>
        <button onClick={handleLogout} className="bg-red-500 text-white px-4 py-2 rounded">Logout</button>
      </div>

      <form onSubmit={handleUpdate} className="bg-white p-6 rounded shadow-md">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {["username", "email", "phone", "place"].map((field) => (
            <div key={field}>
              <label className="block mb-1 font-medium">{field.toUpperCase()}</label>
              <input
                type="text"
                name={field}
                value={formData[field] || ""}
                onChange={handleChange}
                className="w-full border border-gray-300 rounded px-3 py-2"
                disabled={!editMode}
              />
            </div>
          ))}
          <div>
            <label className="block mb-1 font-medium">Profile Picture</label>
            <input
              type="file"
              name="profile"
              onChange={handleChange}
              className="w-full border border-gray-300 rounded px-3 py-2"
              disabled={!editMode}
            />
          </div>
        </div>
        <div className="mt-4 flex space-x-4">
          {editMode ? (
            <>
              <button type="submit" className="bg-green-500 text-white px-4 py-2 rounded">Save</button>
              <button type="button" onClick={() => setEditMode(false)} className="bg-gray-300 px-4 py-2 rounded">Cancel</button>
            </>
          ) : (
            <button type="button" onClick={() => setEditMode(true)} className="bg-blue-500 text-white px-4 py-2 rounded">Edit</button>
          )}
        </div>
      </form>

      <div className="mt-8">
        <h2 className="text-xl font-semibold mb-2">Detection Statistics</h2>
        <p className="mb-2">Total Detections: {total}</p>
        <p className="mb-2">Detection Accuracy: {accuracy}%</p>
        <p className="mb-2">Total Trips: {driver.tripCount || 0}</p>
        <button onClick={() => setShowChart(!showChart)} className="bg-indigo-500 text-white px-4 py-2 rounded">
          {showChart ? "Hide" : "Show"} Chart
        </button>
        {showChart && (
          <ResponsiveContainer width="100%" height={300}>
            <PieChart>
              <Pie
                activeIndex={activeIndex}
                activeShape={renderActiveShape}
                data={data}
                cx="50%"
                cy="50%"
                innerRadius={60}
                outerRadius={80}
                fill="#8884d8"
                dataKey="value"
                onMouseEnter={(_, index) => setActiveIndex(index)}
              >
                {data.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                ))}
              </Pie>
              <Tooltip />
            </PieChart>
          </ResponsiveContainer>
        )}
      </div>

      <div className="mt-8">
        <h2 className="text-xl font-semibold mb-2">Detection Timeline</h2>
        {timeline.length > 0 ? (
          <ul className="list-disc list-inside">
            {timeline.map((time, index) => (
              <li key={index}>{time}</li>
            ))}
          </ul>
        ) : (
          <p>No detection timeline data available.</p>
        )}
      </div>
    </div>
  );
};

export default DriverProfile;
