import React, { useState, useEffect, useRef } from 'react';

const DriverLanding = () => {
  const [status, setStatus] = useState(""); // Holds the status (Active, Drowsy, Sleeping)
  const [isDetecting, setIsDetecting] = useState(false); // Tracks detection state
  const [videoSrc, setVideoSrc] = useState(""); // Video stream source URL
  const videoRef = useRef(null); // Reference for the video element
  const socketRef = useRef(null); // Reference for the WebSocket connection

  useEffect(() => {
    let videoStream;

    // Cleanup function to stop the video stream and WebSocket when component is unmounted or when detection ends
    const cleanup = () => {
      if (socketRef.current) {
        socketRef.current.close();
      }
      if (videoStream) {
        videoStream.getTracks().forEach((track) => track.stop());
      }
      setStatus("");
    };

    return cleanup; // Cleanup on unmount
  }, []);

  // Function to start detection (connect WebSocket and start the video feed)
  const startDetection = () => {
    // Connect WebSocket
    socketRef.current = new WebSocket('ws://127.0.0.1:8000/ws/detect');

    socketRef.current.onopen = () => {
      console.log('WebSocket connected ✅');
    };

    socketRef.current.onmessage = (event) => {
      // Set the status received from the backend (drowsy/sleeping/active)
      setStatus(event.data);
    };

    socketRef.current.onerror = (error) => {
      console.error('WebSocket error: ', error);
    };

    socketRef.current.onclose = () => {
      console.log('WebSocket connection closed');
    };

    // Start video stream from webcam
    if (videoRef.current) {
      navigator.mediaDevices
        .getUserMedia({ video: true })
        .then((stream) => {
          videoStream = stream;
          videoRef.current.srcObject = stream;
          setVideoSrc(URL.createObjectURL(stream));
        })
        .catch((err) => {
          console.error("Error accessing the camera: ", err);
        });
    }

    setIsDetecting(true);
  };

  // Function to stop detection (close WebSocket and stop the video feed)
  const endDetection = () => {
    if (socketRef.current) {
      socketRef.current.close();
    }
    setIsDetecting(false);
    setStatus(""); // Reset the status
    setVideoSrc(""); // Stop showing video stream
  };

  return (
    <div className="min-h-screen bg-brandOrange flex items-center justify-center p-6">
      <div className="w-full max-w-7xl">
        <h6 className="text-center text-4xl font-bold text-black mb-4">
          Hello John
        </h6>

        <div className="relative bg-black rounded-xl overflow-hidden shadow-2xl flex flex-col md:flex-row min-h-[500px]">
          <div className="relative w-full md:w-1/2 overflow-hidden">
            <video
              ref={videoRef}
              autoPlay
              playsInline
              muted
              className="w-full h-full object-cover"
            />
          </div>

          <div className="w-full md:w-1/2 p-8 text-white relative z-10 flex flex-col justify-between">
            <div className="flex justify-end gap-6 text-sm mb-4 flex-wrap">
              <span>Home</span>
              <span>About</span>
            </div>

            <div className="flex flex-col gap-6 items-center justify-center flex-grow">
              <h2 className="text-3xl font-extrabold font-serif italic text-orange-400">
                "Drive Safe, Drive Smart."
              </h2>

              {/* Button to start or end detection */}
              {!isDetecting ? (
                <button
                  className="bg-brandOrange text-black px-6 py-3 rounded-lg text-lg font-semibold w-3/4 flex items-center justify-center"
                  onClick={startDetection}
                >
                  Start Detecting
                </button>
              ) : (
                <button
                  className="bg-red-600 text-black px-6 py-3 rounded-lg text-lg font-semibold w-3/4 flex items-center justify-center"
                  onClick={endDetection}
                >
                  End Detection
                </button>
              )}

              <div className="mt-6 text-xl font-semibold text-white">
                {/* Displaying the status */}
                <p>Status: {status}</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DriverLanding;
