import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { ChevronDown, ChevronUp } from 'lucide-react'; // Optional icons

const UserViewDrivers = () => {
  const [drivers, setDrivers] = useState([]);
  const [sortOrder, setSortOrder] = useState("asc");
  const [filterText, setFilterText] = useState("");

  useEffect(() => {
    axios.get('http://localhost:5000/api/driver')
      .then((res) => {
        setDrivers(res.data);
      })
      .catch((err) => {
        console.error('Failed to fetch drivers', err);
      });
  }, []);

  // Apply sorting
  const sortedDrivers = [...drivers].sort((a, b) => {
    const placeA = a.place.toLowerCase();
    const placeB = b.place.toLowerCase();
    return sortOrder === "asc" ? placeA.localeCompare(placeB) : placeB.localeCompare(placeA);
  });

  // Apply filtering
  const filteredDrivers = sortedDrivers.filter(driver =>
    driver.place.toLowerCase().includes(filterText.toLowerCase())
  );
  const handleBookNow = (driver) => {
  const message = `Hello ${driver.fullName}, I would like to book your service.`;
  const phoneNumber = driver.phone;

  // Example using WhatsApp link (optional)
  // window.open(`https://wa.me/${phoneNumber}?text=${encodeURIComponent(message)}`, '_blank');

  // Or use your backend to send SMS
  axios.post('http://localhost:5000/api/message/send', {
    phone: phoneNumber,
    message,
  })
  .then(res => {
    alert("Message sent successfully!");
  })
  .catch(err => {
    console.error("Failed to send message", err);
    alert("Failed to send message.");
  });
};

  return (
    <div className="min-h-screen bg-brandOrange p-6 pt-20">
      <div className="flex flex-col md:flex-row justify-between items-center mb-6">
        <h1 className="text-4xl font-bold text-black text-center md:text-left mb-4 md:mb-0">
          Driver Profiles
        </h1>

        <div className="flex gap-4 items-center">
          {/* Filter Input */}
          <input
  type="text"
  value={filterText}
  onChange={(e) => setFilterText(e.target.value)}
  placeholder="Search by place..."
  className="px-3 py-2 border rounded placeholder-black  text-black"
/>


          {/* Sort Dropdown */}
          <div className="relative">
            <select
              value={sortOrder}
              onChange={(e) => setSortOrder(e.target.value)}
              className="px-4 py-2 bg-white text-black border border-gray-300 rounded appearance-none"
            >
              <option value="asc">Sort by Place: Ascending</option>
              <option value="desc">Sort by Place: Descending</option>
            </select>
            <div className="absolute top-3 right-3 pointer-events-none">
              {sortOrder === "asc" ? (
                <ChevronUp size={16} />
              ) : (
                <ChevronDown size={16} />
              )}
            </div>
          </div>
        </div>
      </div>

      <div className="grid grid-cols-1 gap-6">
        {filteredDrivers.map((driver, index) => (
          <div
            key={index}
            className="bg-black text-white rounded-xl shadow-lg p-6 flex flex-col md:flex-row items-center justify-between"
          >
            {/* Left - Profile */}
            <div className="flex flex-col items-center w-full md:w-1/3 mb-4 md:mb-0">
              <img
                src={`http://localhost:5000/${driver.profilePicture}`}
                alt="Profile"
                className="w-24 h-24 rounded-full object-cover mb-2"
              />
              <p className="text-lg font-semibold">{driver.username}</p>
              <button
    onClick={() => handleBookNow(driver)}
    className="bg-green-500 hover:bg-green-600 text-white px-4 py-1 rounded text-sm"
  >
    Book Now
  </button>
            </div>

            {/* Middle - Info */}
            <div className="text-center w-full md:w-1/3 mb-4 md:mb-0">
              <p><strong>Full Name:</strong> {driver.fullName}</p>
              <p><strong>Email:</strong> {driver.email}</p>
              <p><strong>Place:</strong> {driver.place}</p>
              <p><strong>Phone:</strong> {driver.phone}</p>
            </div>

            {/* Right - Graph Placeholder */}
            <div className="w-full md:w-1/3 text-center">
              <div className="bg-gray-800 h-24 w-full rounded-lg flex items-center justify-center">
                <p className="text-sm text-gray-400">[Graph Here]</p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default UserViewDrivers;
