import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const DriverProfile = () => {
  const [driver, setDriver] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [formData, setFormData] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("user"));
    if (user) {
      setDriver(user);
      setFormData(user);
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("user");
    navigate("/driver/login");
  };

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: files ? files[0] : value,
    }));
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    const data = new FormData();
    Object.keys(formData).forEach((key) => {
      data.append(key, formData[key]);
    });

    try {
      const res = await fetch(`http://localhost:5000/api/driver/update/${driver._id}`, {
        method: "PUT",
        body: data,
      });
      const updated = await res.json();
      localStorage.setItem("user", JSON.stringify(updated));
      setDriver(updated);
      setEditMode(false);
    } catch (err) {
      alert("Update failed");
    }
  };

  if (!driver) return <p className="text-center mt-10">Loading profile...</p>;

  const detectionTimes = [
    '2025-05-06 08:15 AM',
    '2025-05-06 12:42 PM',
    '2025-05-06 03:10 PM',
    '2025-05-06 05:22 PM',
  ];

  return (
    <div className="min-h-screen bg-brandOrange flex items-center justify-center p-6">
      <div className="grid grid-cols-1 md:grid-cols-2 gap-10 max-w-6xl w-full">
        {/* Profile Section */}
        <div className="bg-black text-white p-10 rounded-2xl shadow-2xl flex flex-col items-center min-h-[450px]">
          <img
            src={driver.profilePicture ? `http://localhost:5000/${driver.profilePicture}` : "/profile.png"}
            alt="Driver"
            className="w-36 h-36 rounded-full object-cover mb-6"
          />
          {editMode ? (
            <form onSubmit={handleUpdate} className="w-full max-w-sm space-y-3" encType="multipart/form-data">
              <input
                name="fullName"
                value={formData.fullName}
                onChange={handleChange}
                placeholder="Full Name"
                className="w-full p-2 rounded text-black"
              />
              <input
                name="email"
                value={formData.email}
                onChange={handleChange}
                placeholder="Email"
                className="w-full p-2 rounded text-black"
              />
              <input
                name="phone"
                value={formData.phone}
                onChange={handleChange}
                placeholder="Phone"
                className="w-full p-2 rounded text-black"
              />
              <input
                name="place"
                value={formData.place}
                onChange={handleChange}
                placeholder="Place"
                className="w-full p-2 rounded text-black"
              />
              <input type="file" name="profilePicture" onChange={handleChange} className="w-full p-2" />
              <div className="flex justify-between pt-2">
                <button type="submit" className="bg-green-500 px-4 py-2 rounded">
                  Save
                </button>
                <button type="button" onClick={() => setEditMode(false)} className="bg-gray-500 px-4 py-2 rounded">
                  Cancel
                </button>
              </div>
            </form>
          ) : (
            <>
              <h2 className="text-2xl font-semibold">{driver.fullName || "No name"}</h2>
<p className="text-sm text-gray-300">{driver.place || "No place provided"}</p>
<div className="mt-6 space-y-2 text-center text-base">
  <p>ðŸ“ž {driver.phone || "No phone provided"}</p>
  <p>âœ‰ {driver.email || "No email provided"}</p>
  <p>ðŸ”“ SMS Alerts: <span className="text-green-400">Active</span></p>
</div>

              <div className="mt-6 flex gap-4">
                
                <button
                  onClick={handleLogout}
                  className="bg-red-500 px-4 py-2 rounded"
                >
                  Logout
                </button>
              </div>
            </>
          )}
        </div>

        {/* Info Sections */}
        <div className="space-y-8">
          {/* Graph Placeholder */}
          <div className="bg-black text-white p-10 rounded-2xl shadow-2xl min-h-[220px]">
            <h3 className="text-xl font-bold mb-6">Driver Detection Over Time</h3>
            <div className="bg-gray-700 rounded h-40 flex items-center justify-center">
              <span className="text-gray-300">[Graph will appear here]</span>
            </div>
          </div>

          {/* Detection Timeline */}
          <div className="bg-black text-white p-10 rounded-2xl shadow-2xl min-h-[220px]">
            <h3 className="text-xl font-bold mb-6">Detection Timeline</h3>
            <ul className="space-y-3 text-lg">
              {detectionTimes.map((time, index) => (
                <li key={index} className="flex items-center">
                  <span className="w-3 h-3 rounded-full bg-green-400 mr-3"></span>
                  {time}
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DriverProfile;
