import React, { useState, useEffect, useRef } from 'react';

const DriverLanding = () => {
  const [status, setStatus] = useState("");
  const [ear, setEAR] = useState(0);
  const [mor, setMOR] = useState(0);
  const [isDetecting, setIsDetecting] = useState(false);
  const videoRef = useRef(null);
  const socketRef = useRef(null);
  const streamRef = useRef(null);

  const getStatusColor = (status) => {
    switch (status.toLowerCase()) {
      case "active": return "text-green-500";
      case "drowsy": return "text-yellow-400";
      case "sleeping": return "text-red-600";
      default: return "text-white";
    }
  };

  const startDetection = () => {
    socketRef.current = new WebSocket('ws://127.0.0.1:8000/ws/detect');

    socketRef.current.onmessage = (event) => {
      const data = JSON.parse(event.data);
      setStatus(data.status);
      setEAR(data.ear);
      setMOR(data.mor);
    };

    navigator.mediaDevices.getUserMedia({ video: true })
      .then((stream) => {
        videoRef.current.srcObject = stream;
        streamRef.current = stream;
      })
      .catch((err) => console.error("Camera error:", err));

    setIsDetecting(true);
  };

  const stopDetection = () => {
    socketRef.current?.close();
    streamRef.current?.getTracks().forEach(track => track.stop());
    setIsDetecting(false);
    setStatus("");
    setEAR(0);
    setMOR(0);
  };

  useEffect(() => {
    return () => stopDetection();
  }, []);

  return (
    <div className="min-h-screen bg-gray-900 text-white flex items-center justify-center p-6">
      <div className="w-full max-w-6xl space-y-6">
        <h1 className="text-3xl font-bold text-center">Driver Drowsiness Detection</h1>
        <div className="flex flex-col md:flex-row bg-black rounded-xl overflow-hidden shadow-2xl">
          <div className="w-full md:w-1/2">
            <video ref={videoRef} autoPlay muted className="w-full h-full object-cover" />
          </div>
          <div className="w-full md:w-1/2 p-6 flex flex-col justify-between">
            <div className="space-y-4">
              <p className="text-lg">EAR: <span className="text-orange-400">{ear}</span></p>
              <p className="text-lg">MOR: <span className="text-orange-400">{mor}</span></p>
              <p className="text-lg">Status: <span className={`font-semibold ${getStatusColor(status)}`}>{status}</span></p>
            </div>
            <div>
              {!isDetecting ? (
                <button onClick={startDetection} className="bg-green-500 text-black font-bold py-2 px-6 rounded-md mt-4 w-full">
                  Start Detection
                </button>
              ) : (
                <button onClick={stopDetection} className="bg-red-600 text-white font-bold py-2 px-6 rounded-md mt-4 w-full">
                  Stop Detection
                </button>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DriverLanding;
