import React, { useState } from 'react';
import DriverProfile from './DriverProfile';

const DriverLanding = () => {
  const [showProfile, setShowProfile] = useState(false);
  const [status, setStatus] = useState("");
  const [socket, setSocket] = useState(null);

  const handleStartDetecting = () => {
    const ws = new WebSocket("ws://localhost:8000/ws/detect"); // FastAPI WebSocket endpoint
    setSocket(ws);

    ws.onopen = () => {
      console.log("WebSocket connection opened");
    };

    ws.onmessage = (event) => {
      console.log("Message from server:", event.data);
      setStatus(event.data);
      // Optional: alert or show a toast
    };

    ws.onerror = (error) => {
      console.error("WebSocket error:", error);
    };

    ws.onclose = () => {
      console.log("WebSocket connection closed");
    };
  };

  return showProfile ? (
    <>
      <button
        className="absolute top-4 left-4 bg-black text-white px-3 py-1 text-sm rounded-md font-semibold shadow-sm hover:bg-gray-200 transition z-50 whitespace-nowrap"
        onClick={() => setShowProfile(false)}
      >
        Back
      </button>
      <DriverProfile />
    </>
  ) : (
    <div className="min-h-screen bg-brandOrange flex items-center justify-center p-6">
      <div className="w-full max-w-7xl">
        {/* Main heading */}
        <h6 className="text-center text-4xl font-bold text-black mb-4">
          Hello John
        </h6>

        {/* Banner container */}
        <div className="relative bg-black rounded-xl overflow-hidden shadow-2xl flex flex-col md:flex-row min-h-[500px]">
          {/* Left image */}
          <div className="relative w-full md:w-1/2 overflow-hidden">
            <img
              src="/car2.jpg"
              alt="Person working"
              className="w-full h-full object-cover"
            />
            {/* Slanted divider */}
            <div className="absolute top-0 right-0 h-full w-2 bg-brandOrange rotate-4 transform origin-left z-9" />
          </div>

          {/* Right content */}
          <div className="w-full md:w-1/2 p-8 text-white relative z-10 flex flex-col justify-between">
            {/* Top Nav */}
            <div className="flex justify-end gap-6 text-sm mb-4 flex-wrap">
              <span>Home</span>
              <span>About</span>
            </div>

            {/* Centered Text */}
            <div className="flex flex-col gap-6 items-center justify-center flex-grow">
              <h2 className="text-3xl font-extrabold font-serif italic text-orange-400">
                "Drive Safe, Drive Smart."
              </h2>

              <button
                className="bg-brandOrange text-black px-6 py-3 rounded-lg text-lg font-semibold w-3/4 flex items-center justify-center"
                onClick={handleStartDetecting}
              >
                Start Detecting
              </button>

              <button
                className="bg-brandOrange text-black px-6 py-3 rounded-lg text-lg font-semibold w-3/4 flex items-center justify-center"
                onClick={() => setShowProfile(true)}
              >
                Profile
              </button>

              {/* Driver Status Display */}
              {status && (
                <div className="bg-black text-white px-4 py-2 rounded-lg shadow mt-4 text-center">
                  <p>
                    Status:{' '}
                    <span className="font-bold text-orange-300">{status}</span>
                  </p>
                </div>
              )}
            </div>

            {/* Bottom icons */}
            <div className="flex items-center gap-4 mt-8">
              <span className="ml-auto text-sm"></span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DriverLanding;
