import React, { useState, useEffect } from 'react';

const DriverLanding = () => {
  const [status, setStatus] = useState('Idle');
  const [socket, setSocket] = useState(null);

  // Open WebSocket connection when "Start Detecting" is clicked
  const handleStartDetecting = () => {
    const ws = new WebSocket('ws://127.0.0.1:8000/ws/detect');
    
    ws.onopen = () => {
      console.log('WebSocket connection established');
      setStatus('Detecting...');
    };

    ws.onmessage = (event) => {
      console.log('Received message:', event.data);
      setStatus(event.data);  // Update status based on received data (like "Frame captured")
    };

    ws.onerror = (error) => {
      console.log('WebSocket error:', error);
      setStatus('Error with WebSocket connection');
    };

    ws.onclose = () => {
      console.log('WebSocket connection closed');
      setStatus('WebSocket connection closed');
    };

    setSocket(ws);  // Save WebSocket reference for future cleanup
  };

  // Cleanup WebSocket when component is unmounted
  useEffect(() => {
    return () => {
      if (socket) {
        socket.close();
      }
    };
  }, [socket]);

  return (
    <div className="min-h-screen bg-brandOrange flex items-center justify-center p-6">
      <div className="w-full max-w-7xl">
        <h6 className="text-center text-4xl font-bold text-black mb-4">Hello John</h6>

        <div className="relative bg-black rounded-xl overflow-hidden shadow-2xl flex flex-col md:flex-row min-h-[500px]">
          <div className="relative w-full md:w-1/2 overflow-hidden">
            <img src="/car2.jpg" alt="Person working" className="w-full h-full object-cover" />
            <div className="absolute top-0 right-0 h-full w-2 bg-brandOrange rotate-4 transform origin-left z-9" />
          </div>

          <div className="w-full md:w-1/2 p-8 text-white relative z-10 flex flex-col justify-between">
            <div className="flex justify-end gap-6 text-sm mb-4 flex-wrap">
              <span>Home</span>
              <span>About</span>
            </div>

            <div className="flex flex-col gap-6 items-center justify-center flex-grow">
              <h2 className="text-3xl font-extrabold font-serif italic text-orange-400">
                "Drive Safe, Drive Smart."
              </h2>

              <button
                className="bg-brandOrange text-black px-6 py-3 rounded-lg text-lg font-semibold w-3/4 flex items-center justify-center"
                onClick={handleStartDetecting}
              >
                Start Detecting
              </button>

              <div className="mt-4 text-white text-xl">{status}</div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DriverLanding;
