import React, { useEffect, useState } from "react";
import axios from "axios";

const UserViewDrivers = () => {
  const [drivers, setDrivers] = useState([]);
  const [sortOrder, setSortOrder] = useState(); // "asc" or "desc"
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchDrivers = async () => {
      setLoading(true);
      try {
        const res = await axios.get(`http://localhost:5000/api/driver?sort=${sortOrder}`);
        setDrivers(res.data);
      } catch (err) {
        console.error("Failed to fetch drivers", err);
      } finally {
        setLoading(false);
      }
    };

    fetchDrivers();
  }, [sortOrder]); // refetch when sortOrder changes

  return (
    <div className="min-h-screen bg-brandOrange p-6 pt-20">
      <h1 className="text-4xl font-bold text-center mb-8 text-black">
        Driver Profiles
      </h1>

      {/* Sorting Button */}
      <div className="flex justify-end mb-6">
  <select
    value={sortOrder}
    onChange={(e) => setSortOrder(e.target.value)}
    className="px-4 py-2 bg-blue text-black border border-gray-300 rounded"
  >
    <option value="asc">Sort by Place: Ascending</option>
    <option value="desc">Sort by Place: Descending</option>
  </select>
</div>

      {loading ? (
        <p className="text-center text-black font-semibold">Loading drivers...</p>
      ) : (
        <div className="grid grid-cols-1 gap-6">
          {drivers.map((driver, index) => (
            <div
              key={index}
              className="bg-black text-white rounded-xl shadow-lg p-6 flex flex-col md:flex-row items-center justify-between"
            >
              {/* Left Column - Profile */}
              <div className="flex flex-col items-center w-full md:w-1/3 mb-4 md:mb-0">
                <img
                  src={driver.profilePicture ? `http://localhost:5000/${driver.profilePicture}` : "/default-profile.png"}
                  alt="Profile"
                  className="w-24 h-24 rounded-full object-cover mb-2"
                />
                <p className="text-lg font-semibold">{driver.username}</p>
              </div>

              {/* Middle Column - Personal Info */}
              <div className="text-center w-full md:w-1/3 mb-4 md:mb-0">
                <p><strong>Email:</strong> {driver.email}</p>
                <p><strong>Place:</strong> {driver.place}</p>
                <p><strong>Phone:</strong> {driver.phone}</p>
              </div>

              {/* Right Column - Graph Placeholder */}
              <div className="w-full md:w-1/3 text-center">
                <div className="bg-gray-800 h-24 w-full rounded-lg flex items-center justify-center">
                  <p className="text-sm text-gray-400">[Graph Here]</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default UserViewDrivers;
