// frontend/src/UserLogin.jsx
import React, { useState } from "react";
import "./login.css";

const UserLogin = () => {
  const [isSignUp, setIsSignUp] = useState(false);

  const [signInData, setSignInData] = useState({ username: "", password: "" });
  const [signUpData, setSignUpData] = useState({
    name: "",
    username: "",
    email: "",
    phone: "",
    password: "",
    confirmPassword: "",
  });

  const handleToggle = () => setIsSignUp(!isSignUp);

  const handleSignIn = () => {
    const { username, password } = signInData;
    if (!username || !password) return alert("Please fill in all fields.");

    fetch("/api/signin", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ username, password }),
    })
      .then((res) => {
        if (res.ok) {
          alert("Sign in successful!");
          setSignInData({ username: "", password: "" });
          window.location.href = "/home";
        } else {
          return res.text().then((text) => alert(text));
        }
      })
      .catch((err) => {
        console.error("Signin error:", err);
        alert("Server error");
      });
  };

  const handleSignUp = () => {
    const { name, username, email, phone, password, confirmPassword } = signUpData;

    if (!name || !username || !email || !phone || !password || !confirmPassword) {
      return alert("Please fill in all fields.");
    }

    if (password !== confirmPassword) {
      return alert("Passwords do not match.");
    }

    fetch("/api/signup", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name, username, email, phone, password }),
    })
      .then((res) => {
        if (res.ok) {
          alert("Sign up successful!");
          setIsSignUp(false);
          setSignUpData({
            name: "",
            username: "",
            email: "",
            phone: "",
            password: "",
            confirmPassword: "",
          });
        } else {
          return res.text().then((text) => alert(text));
        }
      })
      .catch((err) => {
        console.error("Signup error:", err);
        alert("Server error");
      });
  };

  return (
    <div className={`cont ${isSignUp ? "s--signup" : ""}`}>
      <div className="form sign-in">
        <h2>Welcome</h2>
        <label>
          <span>Username</span>
          <input type="text" value={signInData.username} onChange={(e) => setSignInData({ ...signInData, username: e.target.value })} />
        </label>
        <label>
          <span>Password</span>
          <input type="password" value={signInData.password} onChange={(e) => setSignInData({ ...signInData, password: e.target.value })} />
        </label>
        <button type="button" className="submit" onClick={handleSignIn}>Sign In</button>
      </div>

      <div className="sub-cont">
        <div className="img">
          <div className="img__text m--up">
            <h3>Don't have an account? Please Sign up!</h3>
          </div>
          <div className="img__text m--in">
            <h3>If you already have an account, just sign in.</h3>
          </div>
          <div className="img__btn" onClick={handleToggle}>
            <span className="m--up">Sign Up</span>
            <span className="m--in">Sign In</span>
          </div>
        </div>

        <div className="form sign-up">
          <h2>Create your Account</h2>
          {["name", "username", "email", "phone", "password", "confirmPassword"].map((field) => (
            <label key={field}>
              <span>{field === "confirmPassword" ? "Confirm Password" : field.charAt(0).toUpperCase() + field.slice(1)}</span>
              <input
                type={field.includes("password") ? "password" : "text"}
                value={signUpData[field]}
                onChange={(e) => setSignUpData({ ...signUpData, [field]: e.target.value })}
              />
            </label>
          ))}
          <button type="button" className="submit" onClick={handleSignUp}>Sign Up</button>
        </div>
      </div>
    </div>
  );
};

export default UserLogin;
