import React, { useState } from "react";
import "./login.css"; // CSS is in the next section
import { useNavigate } from 'react-router-dom';

const DriverLogin = () => {
  const [isSignUp, setIsSignUp] = useState(false);
  const navigate = useNavigate();
  const [signInData, setSignInData] = useState({
    username: "",
    password: "",
  });

  const [signUpData, setSignUpData] = useState({
    username: "",
    email: "",
    password: "",
    confirmPassword: "",
  });

  const handleToggle = () => {
    setIsSignUp(!isSignUp);
  };

  const handleSignIn = () => {
    const { username, password } = signInData;
    if (username && password) {
      alert("Sign in successful!");
      navigate('/driver/landing');

    } else {
      alert("Please fill in all fields.");
    }
  };

  const handleSignUp = () => {
    const { username, email, password, confirmPassword } = signUpData;
    if (username && email && password && confirmPassword) {
      if (password === confirmPassword) {
        alert("Sign up successful!");
        setIsSignUp(false);
      } else {
        alert("Passwords do not match.");
      }
    } else {
      alert("Please fill in all fields.");
    }
    
  };

  return (
    <div className={`cont ${isSignUp ? "s--signup" : ""}`}>
      <div className="form sign-in">
        <h2>Welcome</h2>
        <label>
          <span>Driver Name</span>
          <input
            type="text"
            value={signInData.username}
            onChange={(e) =>
              setSignInData({ ...signInData, username: e.target.value })
            }
          />
        </label>
        <label>
          <span>Password</span>
          <input
            type="password"
            value={signInData.password}
            onChange={(e) =>
              setSignInData({ ...signInData, password: e.target.value })
            }
          />
        </label>
        <button type="button" className="submit" onClick={handleSignIn}>
          Sign In
        </button>
      </div>

      <div className="sub-cont">
        <div className="img">
          <div className="img__text m--up">
            <h3>Don't have an account? Please Sign up!</h3>
          </div>
          <div className="img__text m--in">
            <h3>If you already have an account, just sign in.</h3>
          </div>
          <div className="img__btn" onClick={handleToggle}>
            <span className="m--up">Sign Up</span>
            <span className="m--in">Sign In</span>
          </div>
        </div>

        <div className="form sign-up">
          <h2>Create your Account</h2>
          <label>
            <span>Username</span>
            <input
              type="text"
              value={signUpData.username}
              onChange={(e) =>
                setSignUpData({ ...signUpData, username: e.target.value })
              }
            />
          </label>
          <label>
            <span>Email</span>
            <input
              type="email"
              value={signUpData.email}
              onChange={(e) =>
                setSignUpData({ ...signUpData, email: e.target.value })
              }
            />
          </label>
          <label>
            <span>Password</span>
            <input
              type="password"
              value={signUpData.password}
              onChange={(e) =>
                setSignUpData({ ...signUpData, password: e.target.value })
              }
            />
          </label>
          <label>
            <span>Confirm Password</span>
            <input
              type="password"
              value={signUpData.confirmPassword}
              onChange={(e) =>
                setSignUpData({
                  ...signUpData,
                  confirmPassword: e.target.value,
                })
              }
            />
          </label>
          <button type="button" className="submit" onClick={handleSignUp}>
            Sign Up
          </button>
        </div>
      </div>
    </div>
  );
};

export default DriverLogin;
