import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const DriverProfile = () => {
  const [driver, setDriver] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [formData, setFormData] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("user"));
    if (user) {
      setDriver(user);
      setFormData(user);
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("user");
    navigate("/driver/login");
  };

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: files ? files[0] : value,
    }));
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    const data = new FormData();
    Object.keys(formData).forEach((key) => {
      data.append(key, formData[key]);
    });

    try {
      const res = await fetch(`http://localhost:5000/api/driver/update/${driver._id}`, {
        method: "PUT",
        body: data,
      });
      const updated = await res.json();
      localStorage.setItem("user", JSON.stringify(updated));
      setDriver(updated);
      setEditMode(false);
    } catch (err) {
      alert("Update failed");
    }
  };

  if (!driver) return <p>Loading profile...</p>;

  return (
    <div style={{ padding: "20px" }}>
      <h2>Driver Profile</h2>
      <button onClick={() => setEditMode((prev) => !prev)}>
        {editMode ? "Cancel Edit" : "Edit Profile"}
      </button>{" "}
      <button onClick={handleLogout}>Logout</button>

      {editMode ? (
        <form onSubmit={handleUpdate} encType="multipart/form-data">
          <input name="fullName" value={formData.fullName} onChange={handleChange} placeholder="Full Name" /><br />
          <input name="email" value={formData.email} onChange={handleChange} placeholder="Email" /><br />
          <input name="phone" value={formData.phone} onChange={handleChange} placeholder="Phone" /><br />
          <input name="place" value={formData.place} onChange={handleChange} placeholder="Place" /><br />
          <input type="file" name="profilePicture" onChange={handleChange} /><br />
          <button type="submit">Save Changes</button>
        </form>
      ) : (
        <div>
          <p><strong>Full Name:</strong> {driver.fullName}</p>
          <p><strong>Username:</strong> {driver.username}</p>
          <p><strong>Email:</strong> {driver.email}</p>
          <p><strong>Place:</strong> {driver.place}</p>
          <p><strong>Phone:</strong> {driver.phone}</p>
          {driver.profilePicture && (
            <img
              src={`http://localhost:5000/${driver.profilePicture}`}
              alt="Profile"
              width="150"
            />
          )}
        </div>
      )}
    </div>
  );
};

export default DriverProfile;
